mapper.update:
update goods
set gdsName = #{gdsName}, cateCode = #{cateCode}, gdsPrice = #{gdsPrice}, gdsDesc = #{gdsDesc}, gdsImg = #{gdsImg}, gdsThumbImg = #{gdsThumbImg}
where gdsCode = #{gdsCode}

update process
set process = 1
where orderId = #{orderId}

update process
set process = 0
where orderId = #{orderId}
