mapper.update:
update goods
   set gdsView = gdsView + 1
   where gdsCode = #{gdsCode}

mapper.insert:
insert into cart (userId, gdsCode, cartStock)
values (#{userId}, #{gdsCode}, #{cartStock})
on duplicate key update cartStock = cartStock + #{cartStock}

mapper.delete:
delete from cart
where gdsCode = #{gdsCode}
and userId = #{userId}

mapper.update:
update cart
set cartStock = cartStock + 1
where gdsCode = #{gdsCode}
and userId = #{userId}

update cart
set cartStock = cartStock - 1
where gdsCode = #{gdsCode}
and userId = #{userId}
